function calculate(){var e=document.Calculator.regularpatients.value,t=document.Calculator.apptcost.value,l=document.Calculator.referralpatients.value,n=50*e,a=.15*n,u=50*l,c=.4*u,r=.6*u,o=(5*n+5*u+10*a)/60/50,m=50*e*.15*.1787+(r-c),s=m*t;document.getElementById("results-patients").innerHTML=Math.ceil(m),document.getElementById("results-money").innerHTML="$"+Math.ceil(s),document.getElementById("results-time").innerHTML=Math.ceil(o)+" hrs",document.getElementById("calculate-button").innerHTML="Recalculate",document.getElementById("results").style.display="block"}